PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE capteurs (
	id INTEGER NOT NULL, 
	mac_addr VARCHAR(20) NOT NULL, 
	location VARCHAR(20) NOT NULL, 
	name VARCHAR(15) NOT NULL, 
	PRIMARY KEY (id)
);
CREATE TABLE data_environnement (
	id INTEGER NOT NULL, 
	"idCapteur" INTEGER NOT NULL, 
	"timeStamp" DATETIME NOT NULL, 
	temperature FLOAT, 
	hygrometrie FLOAT, 
	batterie INTEGER, 
	PRIMARY KEY (id), 
	FOREIGN KEY("idCapteur") REFERENCES capteurs (id)
);
COMMIT;
